import { Injectable } from '@angular/core';
import { HttpModule, Http, Response, Headers, RequestOptions } from '@angular/http';
import { LazyLoadEvent } from 'primeng/primeng';
import { Observable } from 'rxjs/Observable';
import 'rxjs/add/observable/throw';
import 'rxjs/add/operator/catch';
import 'rxjs/add/operator/map';
import { MessageService } from '../../service/message.service';
import { PageResponse, PageRequestByExample } from '../../support/paging';
import { Race } from './race';

@Injectable()
export class RaceService {

    private options = new RequestOptions({ headers: new Headers({ 'Content-Type': 'application/json' }) });

    constructor(private http: Http, private messageService : MessageService) {}

    /**
     * Get a Race by id.
     */
    getRace(id : any) : Observable<Race> {
        return this.http.get('/api/races/' + id)
            .map(response => new Race(response.json()))
            .catch(this.handleError);
    }

    /**
     * Update the passed race.
     */
    update(race : Race) : Observable<Race> {
        let body = JSON.stringify(race);

        return this.http.put('/api/races/', body, this.options)
            .map(response => new Race(response.json()))
            .catch(this.handleError);
    }

    /**
     * Load a page (for paginated datatable) of Race using the passed
     * race as an example for the search by example facility.
     */
    getPage(race : Race, event : LazyLoadEvent) : Observable<PageResponse<Race>> {
        let req = new PageRequestByExample(race, event);
        let body = JSON.stringify(req);

        return this.http.post('/api/races/page', body, this.options)
            .map(response => {
                let pr : any = response.json();
                return new PageResponse<Race>(pr.totalPages, pr.totalElements, Race.toArray(pr.content));
            })
            .catch(this.handleError);
    }

    /**
     * Performs a search by example on 1 attribute (defined on server side) and returns at most 10 results.
     * Used by RaceCompleteComponent.
     */
    complete(query : string) : Observable<Race[]> {
        let body = JSON.stringify({'query': query, 'maxResults': 10});
        return this.http.post('/api/races/complete', body, this.options)
            .map(response => Race.toArray(response.json()))
            .catch(this.handleError);
    }

    /**
     * Delete an Race by id.
     */
    delete(id : any) {
        return this.http.delete('/api/races/' + id).catch(this.handleError);
    }

    // sample method from angular doc
    private handleError (error: any) {
        // TODO: seems we cannot use messageService from here...
        let errMsg = (error.message) ? error.message :
        error.status ? `Status: ${error.status} - Text: ${error.statusText}` : 'Server error';
        console.error(errMsg); // log to console instead
        if (error.status === 401 ) {
            window.location.href = '/';
        }
        return Observable.throw(errMsg);
    }
}
